mapper.insert:
INSERT INTO 
	eval_total (
		eval_total_code
		, institution_code
		, institution_name
		, eval_total_type
		, eval_total_year
		, eval_total_date
	)
VALUES (
	#{evalTotalCode}
	, #{institutionCode}
	, (
	SELECT
		institution_name
	FROM
		institution
	WHERE
		institution_code = #{institutionCode}
	)
	, #{evalTotalType}
	, #{evalTotalYear}
	, now()
	)
