mapper.insert:
INSERT INTO lecture (
	lecture_code
	,notice_lecture_code
	,notice_lecture_title
	,info_lecture_code
	,institution_code
	,institution_name
	,member_teacher_code
	,member_teacher_name
	,notice_lecture_start_date
	,notice_lecture_end_date
	,lecture_class_days
	,lecture_date
) VALUES (
	#{lectureCode}
	,#{noticeLectureCode}
	,(SELECT
			notice_lecture_title
		FROM
			notice_lecture
		WHERE 
			notice_lecture_code = #{noticeLectureCode}
		)
	,#{infoLectureCode}
	,#{institutionCode}
	,(SELECT
			institution_name
		FROM
			institution
		WHERE 
			institution_code = #{institutionCode}
		)
	,#{memberTeacherCode}
	,(SELECT
			member_teacher_name
		FROM 
			member_teacher
		WHERE 
			member_teacher_code = #{memberTeacherCode}
	,#{lectureStartDate}
	,#{lectureEndDate}
	,#{lectureClassDays}
	,now()
)
