mapper.insert:

	INSERT INTO
		member (
		member_code
		, member_name
		, member_registration_number_front
		, member_registration_number_back
		, member_phone_number
		, member_postcode
		, member_address
		, member_address_desc
		, member_rank
		, member_date)
	VALUES (
		#{memberCode}
		, #{memberName}
		, #{memberRegistrationNumberFront}
		, #{memberRegistrationNumberBack}
		, #{memberPhoneNumber}
		, #{memberPostcode}
		, #{memberAddress}
		, #{memberAddressDesc}
		, #{memberRank}
		, now()
	)

